INSERT INTO items (id, name, level, type, img_url, description) VALUES (44, 'Épée de Boisaille', 7, 'Épée', 'https://api.dofusdb.fr/img/items/6007.png', 'Épée de simple facture, elle ne fera pas de votre héros une légende.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (303, 'Bois de Frêne', 1, 'Bois', 'https://api.dofusdb.fr/img/items/38017.png', 'Le Bois de Frêne n''est pas un bois de qualité, mais il est abondant et pousse relativement vite. Attention tout de même à ne pas le stocker près d''une forte source de chaleur, il est inflammable.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (16512, 'Plume Chimérique', 1, 'Plume', 'https://api.dofusdb.fr/img/items/53661.png', 'Tentez donc d''attraper la plume d''un Tofu Chimérique en lui courant après, ça vous fera les jambes !') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (49, 'Épée de Fouraille', 15, 'Épée', 'https://api.dofusdb.fr/img/items/6002.png', 'Une épée bien forgée, intéressante pour les novices.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (377, 'Épine du Champ Champ', 18, 'Champignon', 'https://api.dofusdb.fr/img/items/15049.png', 'Cette épine venimeuse protège le Champ Champ des attaques au corps à corps, mais l''empêche en revanche de faire des câlins sans tuer ceux qu''il aime. Heureusement pour lui, le Champ Champ utilise du Sporme qu''il envoie à distance pour se reproduire.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (1673, 'Patte d''Arakne Magique', 18, 'Patte', 'https://api.dofusdb.fr/img/items/103392.png', 'Cette patte d''Arakne, n''est pas comme toutes les autres, elle bouge toute seule et semble pleine de vie, même une fois arrachée à sa maîtresse. Une nouvelle vie pourrait commencer pour cette patte qui semble attirée par la couture.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (55, 'Épée d''Alle', 26, 'Épée', 'https://api.dofusdb.fr/img/items/6001.png', 'Cette épée très prisée des jeunes Iops vous permettra peut-être de faire vos preuves. Mais attention, ne vous attachez pas à elle, car on finit toujours par perdre l''épée d''Alle.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (300, 'Graine de Pavot', 18, 'Graine', 'https://api.dofusdb.fr/img/items/58011.png', 'Cette graine confère des vertus hallucinogènes aux pains prisés des aventuriers qui désirent se reposer après un combat difficile.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (364, 'Peau de Larve Verte', 18, 'Peau', 'https://api.dofusdb.fr/img/items/59034.png', 'Cette peau toute lisse constitue un excellent ingrédient pour les potions magiques.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (2659, 'Pétale de Tournesol Sauvage', 18, 'Fleur', 'https://api.dofusdb.fr/img/items/35632.png', 'Ce pétale bouge. Oui, il bouge en fonction du soleil. Mettez-le dans votre poche droite le matin, il sera dans votre poche gauche le soir venu.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (58, 'La Triste Lame', 21, 'Épée', 'https://api.dofusdb.fr/img/items/6005.png', 'Il s''agit d''une épée ayant baigné dans les larmes d''un jeune Mulou. Beaucoup de guerriers pensent qu''ainsi bénie, leur arme apportera chance et courage sur les champs de bataille.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (306, 'Fleur de Pissenlit Diabolique', 18, 'Fleur', 'https://api.dofusdb.fr/img/items/35018.png', 'Correctement bouillie puis séchée, cette fleur perd ses propriétés toxiques et peut se consommer en tisane, mais la fleur de Pissenlit Diabolique est surtout utilisée pour amuser la jeunesse en quête de romantisme. En effet, qui n''a jamais soufflé sur une fleur de Pissenlit Diabolique pour envoyer dans le cou de l''être tant convoité, ces magnifiques graines, douces comme la caresse d''une éclaircie sur les feuilles d''un charme encore imprégnées de rosée ? Peut-être les Iops, qui au lieu de souffler, ont tendance à aspirer les graines et à s''étouffer lamentablement dans d''atroces convulsions.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (747, 'Aluminite', 20, 'Alliage', 'https://api.dofusdb.fr/img/items/40660.png', 'Cet alliage est d''une légèreté impressionnante, ce qui en fait un métal de choix pour les petites dagues ou les épées courtes.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (62, 'Épée d''Ha', 17, 'Épée', 'https://api.dofusdb.fr/img/items/6006.png', 'Conçue par un forgeron qui voyait double, cette épée est très populaire à Brâkmar. Il n''y a pas un milicien qui ne s''écrie « Je veux mon épée d''Ha ! » Sa seule vue fait loucher le plus courageux des adversaires.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (366, 'Bec du Tofu', 13, 'Os', 'https://api.dofusdb.fr/img/items/47037.png', 'Ce ridicule petit bec de Tofu, d''apparence anodine, est cependant très aiguisé. Les archers d''Amakna les utilisent depuis des lustres comme pointes de flèches.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (65, 'Goultard', 40, 'Épée', 'https://api.dofusdb.fr/img/items/6017.png', 'L''épée Goultard a été trempée dans le sang d''un Dragon Cochon. Le héros qui supportera l''odeur dégagée par cette puissante épée pourra se hisser à la hauteur des plus grands.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (407, 'Pic de Prespic', 26, 'Os', 'https://api.dofusdb.fr/img/items/47134.png', 'Pour se piquer au jeu de cache-cache, rien de mieux qu''un pic de prespic.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (881, 'Laine de Ramane Strubien', 23, 'Laine', 'https://api.dofusdb.fr/img/items/57208.png', 'Cette laine est extrêmement douce, car elle provient du duvet d''un Ramane. Attention tout de même, elle est encore imprégnée d''une odeur qui pourrait inciter la mère du Ramane à vous lécher de la tête aux pieds.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (6841, 'Œil de Pikdoa', 26, 'Œil', 'https://api.dofusdb.fr/img/items/25151.png', 'Collé à un costume d''Halouine, cet œil simulera à merveille une tumeur cancéreuse.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (66, 'La crinière de Badoul', 33, 'Épée', 'https://api.dofusdb.fr/img/items/6011.png', 'Cette épée appartenait au célèbre Badoul, vaillant guerrier ayant perdu la boule lors d''un combat.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (387, 'Défense du Sanglier', 24, 'Os', 'https://api.dofusdb.fr/img/items/47059.png', 'Cette défense permet au Sanglier de se défendre et accessoirement de curer son groin débusqueur de glands. N''oubliez donc pas de la nettoyer à l''eau vive avant d''en faire de la poudre.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (885, 'Laine de Boufton Noir', 26, 'Laine', 'https://api.dofusdb.fr/img/items/57210.png', 'Cette laine rugueuse n''est pas très utilisée par les tailleurs, elle est en revanche très appréciée des Enutrofs, qui s''en servent pour cacher leur calvitie naissante.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (2468, 'Estomac de Ouassingue', 36, 'Ressource diverse', 'https://api.dofusdb.fr/img/items/15506.png', 'À l''intérieur de cet estomac se cache une surprise. Petit doigt d''Enutrof, bout de nez d''Osamodas... On pourrait quasiment se reconstruire un héros avec tous ces morceaux.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (67, 'Le Tranchant Infernal', 22, 'Épée', 'https://api.dofusdb.fr/img/items/6013.png', 'Tranchante et infernale : tels ont été les mots de la succube qui l''a forgée. À moins que ce ne soit les mots utilisés par Vald pour décrire sa sœur, plus personne ne sait.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (2455, 'Sang de Bouftou', 19, 'Liquide', 'https://api.dofusdb.fr/img/items/15499.png', 'Impossible d''aller chasser sans se passer de ce Sang de Bouftou tellement son odeur attire les Bouftous les plus gros. Il attire aussi très efficacement les Moskitos sans que l''on sache encore pourquoi.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (6899, 'Plume de Piou Vert', 13, 'Plume', 'https://api.dofusdb.fr/img/items/53116.png', 'Cette petite plume de Piou a été directement arrachée du derrière d''un pauvre Piou qui n''avait rien demandé.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (13487, 'Serviette de Plage', 18, 'Étoffe', 'https://api.dofusdb.fr/img/items/55130.png', 'Cette serviette peut être utilisée pour accompagner les bains de soleil, pour frictionner les corps bronzés des Sadidettes ou pour éponger les dettes de jeu des Ecaflips. Son pouvoir absorbant en fait aussi une excellente serpillière.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (70, 'Amulette du Hibou', 8, 'Amulette', 'https://api.dofusdb.fr/img/items/1001.png', 'Cette amulette augmente l''intelligence de son porteur.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (385, 'Bave de Bouftou', 7, 'Liquide', 'https://api.dofusdb.fr/img/items/15057.png', 'Cette bave purulente tire son odeur du fait que le Bouftou passe son temps à se lécher la laine, ce qui est également à l''origine de l''haleine exécrable du Bouftou.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (16524, 'Relique d''Incarnam', 5, 'Ressource diverse', 'https://api.dofusdb.fr/img/items/164068.png', 'Si l''on en croit la légende, ces reliques auraient le pouvoir de ramener les morts à la vie. Cependant, quand on voit les Chafers d''Incarnam, on peut douter de leur efficacité...') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (77, 'Amulette de l''Ours', 8, 'Amulette', 'https://api.dofusdb.fr/img/items/1005.png', 'Cette amulette augmente la force de son porteur.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (1984, 'Cendres Éternelles', 1, 'Poudre', 'https://api.dofusdb.fr/img/items/48671.png', 'Voilà tout ce qui reste d''une âme infortunée qui a croisé votre route. La créature a commis l''erreur de penser qu''en étant dénuée de corps, elle pourrait vous échapper. Tant de naïveté vous fait sourire.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (84, 'Amulette du Mulou', 8, 'Amulette', 'https://api.dofusdb.fr/img/items/1006.png', 'Cette amulette augmente la vitalité de son porteur.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (91, 'Arc de Boisaille', 7, 'Arc', 'https://api.dofusdb.fr/img/items/2001.png', 'Arc de simple facture, il ne fera pas de votre héros une légende.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (421, 'Ortie', 1, 'Plante', 'https://api.dofusdb.fr/img/items/36642.png', 'À chacun de ses anniversaires, il est de coutume de pousser mémé dans un champ de cette plante.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (16522, 'Peau de Gloot', 2, 'Peau', 'https://api.dofusdb.fr/img/items/164066.png', 'Tel un ballon de baudruche, cette peau renferme l''eau qui permet aux Gloots de survivre. Si vous voulez en venir à bout, une simple aiguille devrait suffire.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (97, 'Dagues de Boisaille', 7, 'Dague', 'https://api.dofusdb.fr/img/items/5010.png', 'Dagues de simple facture, elles ne feront pas de votre héros une légende.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (16515, 'Queue de Chapardam', 4, 'Queue', 'https://api.dofusdb.fr/img/items/65767.png', 'Portée en écharpe ou utilisée comme un fouet, cette queue saura trouver de multiple fonctions entre vos mains expertes.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (103, 'Anneau de Sagesse', 9, 'Anneau', 'https://api.dofusdb.fr/img/items/9007.png', 'Cet anneau renforce la sagesse de son porteur.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (310, 'Os de Chafer', 5, 'Os', 'https://api.dofusdb.fr/img/items/47015.png', 'Cet os est presque incassable, mais il devient tout mou au contact de l''eau, ce qui facilite son utilisation par les alchimistes.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (112, 'Anneau de Chance', 9, 'Anneau', 'https://api.dofusdb.fr/img/items/9009.png', 'Cet anneau renforce la chance de son porteur.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (16523, 'Volve d''Aminite', 5, 'Champignon', 'https://api.dofusdb.fr/img/items/119014.png', 'Si vous vous demandez ce qu''est une volve, c''est que vous devez être un homme, assurément.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (121, 'Anneau d''Agilité', 9, 'Anneau', 'https://api.dofusdb.fr/img/items/9001.png', 'Cet anneau augmente l''agilité de son porteur.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (312, 'Fer', 1, 'Minerai', 'https://api.dofusdb.fr/img/items/39024.png', 'Le fer est très utile pour les forgerons et pour ceux qui utilisent du métal, certains s''en servent même pour repasser.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (127, 'Bottes de Bowisse', 50, 'Bottes', 'https://api.dofusdb.fr/img/items/11002.png', 'Ces bottes exceptionnelles vous permettront de gagner un point de mouvement durant les combats.
INSERT INTO items (id, name, level, type, img_url, description) VALUES (2571, 'Patte de Tofukaz', 36, 'Patte', 'https://api.dofusdb.fr/img/items/103577.png', 'Comment un Tofukaz, et vous devez vous poser sûrement la même question, fait-il pour être aussi agile avec ces deux malheureux doigts de patte? Le mystère reste entier.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (11814, 'Moustache de Boostache', 40, 'Poil', 'https://api.dofusdb.fr/img/items/54680.png', 'Le Boostache a toujours été complexé de ne pas avoir de poils au menton... et de ne pas avoir de menton du tout, d''ailleurs ! C''est donc pour compenser qu''il s''est laissé pousser ces superbes moustaches.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (12737, 'Galet cramoisi', 50, 'Galet', 'https://api.dofusdb.fr/img/items/15290.png', 'Ce caillou permet de faire de très bons ricochets sur les lacs ou les crânes des plus faibles que vous.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (13098, 'Casque du Chafer Primitif', 36, 'Vêtement', 'https://api.dofusdb.fr/img/items/15798.png', 'Ce casque est représentatif de son porteur : est en bois à l''heure où la mode est aux métaux, pierres précieuses et autres alliages avancés, a une corne montée à l''envers, n''offre aucune protection du visage... Pas de doute, le Chafer Primitif l''est autant qu''il en a l''air. Dommage qu''il ne soit pas à votre taille, il vous irait à ravir.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (130, 'L''Ecrabouilleur de Iop', 27, 'Bottes', 'https://api.dofusdb.fr/img/items/11007.png', 'Ces bottes sont idéales lorsqu''il s''agit d''asséner un coup de grâce.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (2491, 'Œil d''Arakmuté', 13, 'Œil', 'https://api.dofusdb.fr/img/items/109516.png', 'Inquiétant, mais palpitant, cet Œil d''Aramuté ne reflète pas vraiment ce que l''on attend. Par exemple, au lieu de vous voir dedans, vous voyez votre homologue Dopeul. Allez comprendre.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (2563, 'Peau de Larve Jaune', 18, 'Peau', 'https://api.dofusdb.fr/img/items/25138.png', 'Cette peau toute lisse constitue un excellent ingrédient pour les potions magiques.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (13726, 'Conque Marine', 18, 'Ressource diverse', 'https://api.dofusdb.fr/img/items/15813.png', 'Collez votre oreille contre cette conque, et vous entendrez le bruit de l''océan. Soufflez dans cette conque, et vous entendrez le bruit des bottes des miliciens venus vous arrêter pour cassage de tympans.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (134, 'Baguette de Boisaille', 3, 'Baguette', 'https://api.dofusdb.fr/img/items/3008.png', 'Baguette de simple facture, elle ne fera pas de votre héros une légende.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (140, 'Bâton de Boisaille', 9, 'Bâton', 'https://api.dofusdb.fr/img/items/4001.png', 'Bâton de simple facture, il ne fera pas de votre héros une légende.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (16518, 'Feu Intérieur', 1, 'Ressource diverse', 'https://api.dofusdb.fr/img/items/164067.png', 'Après des années de méditation, vous serez peut-être capable de révéler votre feu intérieur. Si vous n''avez pas envie d''attendre, vous pouvez tout aussi bien martyriser des Feux spirituels.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (147, 'Marteau de Boisaille', 6, 'Marteau', 'https://api.dofusdb.fr/img/items/7001.png', 'Marteau de simple facture, il ne fera pas de votre héros une légende.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (153, 'Pelle de Boisaille', 6, 'Pelle', 'https://api.dofusdb.fr/img/items/8001.png', 'Pelle de simple facture, elle ne fera pas de votre héros une légende.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (156, 'Ceinture de Puissance', 31, 'Ceinture', 'https://api.dofusdb.fr/img/items/10019.png', 'La Ceinture de Puissance rend son porteur particulièrement puissant lors des combats, mais son poids réduit de manière conséquente son agilité.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (746, 'Ébonite', 40, 'Alliage', 'https://api.dofusdb.fr/img/items/40658.png', 'Cet alliage très rare permet de fabriquer des objets très rares et souvent mortels. En effet, l''ébonite est un métal qui une fois rouillé, devient un véritable poison.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (157, 'La Gougnole', 17, 'Amulette', 'https://api.dofusdb.fr/img/items/1007.png', 'La Gougnole a été créée dans de la pierre de lune ayant trempé 5000 ans dans un baril de bière. Inévitablement, cela lui a conféré de terribles pouvoirs.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (363, 'Peau de Larve Orange', 18, 'Peau', 'https://api.dofusdb.fr/img/items/59033.png', 'Cette peau toute lisse constitue un excellent ingrédient pour les potions magiques.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (158, 'La Destinée Dorée', 12, 'Amulette', 'https://api.dofusdb.fr/img/items/1009.png', 'La Destinée Dorée permet au héros qui la porte d''augmenter considérablement sa vitalité et son agilité.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (16440, 'Ferrite', 1, 'Alliage', 'https://api.dofusdb.fr/img/items/40712.png', 'Ce drôle d''alliage est fabriqué par les mineurs débutants pour s''exercer.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (159, 'La Broche Céleste', 40, 'Amulette', 'https://api.dofusdb.fr/img/items/1008.png', 'On dit que cette broche a été créée par l''illustre forgeron Boulgur, qui a la réputation de forger les armes des dieux.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (2467, 'Rondelles de Milirat Strubien', 26, 'Ressource diverse', 'https://api.dofusdb.fr/img/items/63373.png', 'L''expression "se faire péter la rondelle" provient des éboueurs qui avaient pour seule nourriture de la viande de milirat, laquelle est réputée pour provoquer d''intenses et spectaculaires crises d''aérophagie.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (6845, 'Casque de Martoa', 26, 'Vêtement', 'https://api.dofusdb.fr/img/items/25149.png', 'Comme il n''est jamais à l''abri d''une chute de pierres, le Martoa sort toujours couvert.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (12745, 'Substrat de Bocage', 40, 'Substrat', 'https://api.dofusdb.fr/img/items/12729.png', 'L''amicale des anciens bénévoles pompiers retraités mais valeureux du château d''Allister vous recommande de ne pas utiliser ce liquide pour éteindre les incendies. Sauf si vous aimez ajouter de l''huile sur le feu.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (160, 'Le Flan des Étoiles', 61, 'Amulette', 'https://api.dofusdb.fr/img/items/1010.png', 'Cet objet a été moulé à partir d''un mélange d''œufs célestes et de poussières d''étoiles lactées. À conserver au frais.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (648, 'Aile de Bourdard', 56, 'Aile', 'https://api.dofusdb.fr/img/items/104750.png', 'Cette aile continue de vibrer frénétiquement malgré le fait qu''elle ait été violemment détachée du corps de son propriétaire. C''est amusant au début, mais cela devient limite ennuyeux quand on en a plein le sac.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (650, 'Duvet de Bourdard', 56, 'Poil', 'https://api.dofusdb.fr/img/items/54709.png', 'À l''adolescence, le corps du Bourdard se recouvre progressivement d''un doux duvet, ce qui est paradoxal au comportement agressif qu''il adopte à la même période.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (748, 'Magnésite', 60, 'Alliage', 'https://api.dofusdb.fr/img/items/40659.png', 'Cet alliage original est presque magique, il brille et vibre la nuit, ce qui peut être suffisant pour effrayer un Bwork un peu trop crédule.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (2482, 'Charnière Cassée', 56, 'Ressource diverse', 'https://api.dofusdb.fr/img/items/15942.png', 'L''usure causée par les nombreuses ouvertures et fermetures consécutives du couvercle de la malle a finalement eu raison de cette pauvre charnière de sécurité. Ça a dû être très douloureux.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (11696, 'Cagoule du Kanniboul Ebil', 60, 'Vêtement', 'https://api.dofusdb.fr/img/items/15784.png', 'Rares sont ceux qui le savent, mais sous son masque, le Kanniboul Ebil porte aussi une cagoule ! Maintenant que vous l''avez entre les mains, vous allez pouvoir le prouver. Reste à savoir si l''on vous croira...') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (161, 'La Célébrité', 27, 'Amulette', 'https://api.dofusdb.fr/img/items/1009.png', 'Cette puissante amulette a tout pour rendre son porteur célèbre.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (1674, 'Champignon Luidegît', 35, 'Champignon', 'https://api.dofusdb.fr/img/items/15390.png', 'Ce héros a été envoûté par le Chêne Mou, mais la magie un peu aléatoire de ce dernier a fait qu''il s''est transformé en champignon. Les Champas étant assez idiots, ils le vénèrent et le protègent jusqu''à la mort.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (165, 'Baguette de Glace', 11, 'Baguette', 'https://api.dofusdb.fr/img/items/3002.png', 'Une baguette bien sculptée, intéressante pour les novices.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (180, 'La Baguette des Limbes', 105, 'Baguette', 'https://api.dofusdb.fr/img/items/3007.png', 'Cette baguette provient des Limbes de Syrastass. La légende raconte qu''elle permet de faire fleurir des champs entiers de pâquerettes à la simple demande de son porteur.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (315, 'Diamant', 50, 'Pierre précieuse', 'https://api.dofusdb.fr/img/items/50025.png', 'Tous les bijoutiers d''Amakna raffolent de cette pierre qui demande une excellente dextérité pour être travaillée. Il faut souvent s''aventurer dans de dangereuses mines pour les trouver, mais heureusement, les diamants sont éternels.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (2620, 'Rhum de Qualité', 90, 'Liquide', 'https://api.dofusdb.fr/img/items/15177.png', 'Ce Rhum doit sortir d''une cave très prisée. À moins qu''il soit resté dans une poche pendant de bonnes générations.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (2805, 'Testicules Magiques de Muloubard', 96, 'Ressource diverse', 'https://api.dofusdb.fr/img/items/15650.png', 'Personne ne sait exactement quand et comment cela s''est produit, mais un jour, ces testicules se sont vus dotés de pouvoirs magiques assez obscurs, il faut bien le reconnaître.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (8384, 'Étoffe Magique de Moon', 100, 'Étoffe', 'https://api.dofusdb.fr/img/items/55722.png', 'À en juger par les petites étoiles qui brillent autour d''elle, cette étoffe semble magique. Vous n''y croyez pas ? Il faudra bien que vous reconnaissiez un jour ou l''autre que vous êtes trop stupide pour déterminer vous-même ce qui est magique ou non. Ne suivez pas votre instinct de pourfendeur d''Astrubiens, faites confiance aux descriptions.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (14512, 'Plume de Gobvious', 94, 'Plume', 'https://api.dofusdb.fr/img/items/53658.png', 'Complètement déplumé, le plumeau de Gobvious pourra toujours servir de cravache improvisée lors de vos soirées cagoulées.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (14517, 'Slip troué de Dramak', 100, 'Vêtement', 'https://api.dofusdb.fr/img/items/164026.png', 'Pour ne pas être moqué, Dramak a mis à exécution son plan machiavélique. Il a pris le contrôle d''un ménestrel peu talentueux pour lui écrire une chanson qui le mettrait à la pointe de la mode. C''est ainsi qu''en 643, les Astrubiens se sont mis à fredonner « Quand je lâche une caisse, je troue mon slip » puis à joindre le geste à la parole.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (14921, 'Étoffe Mystérieuse', 1, 'Étoffe', 'https://api.dofusdb.fr/img/items/15848.png', 'Cette étoffe possède des propriétés moulantes et élastiques hors du commun. Les rumeurs les plus folles lui accordent des pouvoirs surhumains, comme se faire temporairement oublier de ses beaux-parents ou penser à récupérer les enfants à la sortie de l''école pendant toute une semaine. Ceci explique pourquoi on l''utilise dans la confection d''équipements plus ou moins puissants.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (181, 'Baguette des Cieux', 51, 'Baguette', 'https://api.dofusdb.fr/img/items/3006.png', 'Cette baguette fut bénie par Helle du haut des cieux. Elle procure à son porteur agilité et vitalité dans les batailles.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (2288, 'Patte de Wabbit', 48, 'Patte', 'https://api.dofusdb.fr/img/items/103440.png', 'Bon nombre de touristes vont sur l''île des Wabbits essentiellement pour se munir d''une Patte de Wabbit car elle porte bonheur. Malheureusement, ça n''est pas le cas, mais il ne faut pas le dire, le tourisme en serait fort pénalisé.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (8738, 'Sacrum magistral', 50, 'Os', 'https://api.dofusdb.fr/img/items/47011.png', 'Alors que le Corailleur vous offrait un os de son postérieur, le Corailleur Magistral vous permet quant à lui de prendre possession d''une autre partie de son anatomie, mais à peu près dans le même secteur.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (8747, 'Pétale Passaoh', 43, 'Fleur', 'https://api.dofusdb.fr/img/items/35004.png', 'La couleur de ce pétale témoigne de sa provenance douteuse. L''abus d''alcool chez les disciples d''Otomaï se perçoit même dans leurs créations.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (183, 'Bâton Feuillu', 12, 'Bâton', 'https://api.dofusdb.fr/img/items/4002.png', 'Un bâton bien taillé, intéressant pour les novices.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (388, 'Poils d''Arakne Malade', 13, 'Poil', 'https://api.dofusdb.fr/img/items/54060.png', 'Ces poils particulièrement résistants sont utilisés entre autres pour la confection de pinceaux et de balais. Certaines personnes subissent d''importantes allergies au contact de ces poils, il est donc recommandé de ne pas s''y frotter avec vigueur.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (16487, 'Bois Vermoulu', 18, 'Bois', 'https://api.dofusdb.fr/img/items/38683.png', 'Il y a moins de traces de vers dans ce bois que dans celui du postérieur de votre dernière conquête.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (188, 'Bâton Kouyu', 8, 'Bâton', 'https://api.dofusdb.fr/img/items/4003.png', 'Ce bâton augmente la sagesse de son porteur. Peut entraîner une hypertrophie.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (194, 'Bâton Spirituel', 16, 'Bâton', 'https://api.dofusdb.fr/img/items/4008.png', 'Ce bâton augmente la vitalité de son porteur et peut, le cas échéant, être mangé.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (365, 'Patte d''Arakne', 18, 'Patte', 'https://api.dofusdb.fr/img/items/103035.png', 'Une patte très fine, mais très poilue. Très prisée des alchimistes pour ses propriétés euphorisantes, c''est la petite faiblesse qui les perdra.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (6902, 'Plume de Piou Jaune', 13, 'Plume', 'https://api.dofusdb.fr/img/items/53118.png', 'Cette petite plume de Piou a été directement arrachée du derrière d''un pauvre Piou qui n''avait rien demandé.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (200, 'God Rod', 67, 'Bâton', 'https://api.dofusdb.fr/img/items/4007.png', 'Le God Rod est une arme terrible. Très puissant, il permet à son porteur de se montrer digne des meilleurs lorsqu''il a une petite baisse de régime.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (1891, 'Peau de serpentin', 56, 'Peau', 'https://api.dofusdb.fr/img/items/59361.png', 'N''avez-vous jamais rêvé d''un magnifique petit sac en peau de Serpentin ? Non ? Vous avez bien raison, car sa résistance laisse à désirer.') ON CONFLICT (id) DO NOTHING;
INSERT INTO items (id, name, level, type, img_url, description) VALUES (2496, 'Queue de Rat d''Hyoactif', 56, 'Queue', 'https://api.dofusdb.fr/img/items/65521.png', 'Tranchée convenablement, cette queue a le goût de saucisson. Ce qui est étrange, c''est que si vous coupez de trop grosses tranches, le goût de saucisson disparaît totalement. Encore une énigme culinaire de plus dans ce monde.') ON CONFLICT (id) DO NOTHING;
